import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const API_URL = "http://localhost:5000/movies";

const MovieForm = ({ onSubmit, movies }) => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [movie, setMovie] = useState({ title: "", genre: "", year: "", poster: "", trailer: "", director: "" });

    useEffect(() => {
        if (id) {
            const existingMovie = movies.find((m) => m._id === id);
            if (existingMovie) setMovie(existingMovie);
        }
    }, [id, movies]);

    const handleChange = (e) => {
        setMovie({ ...movie, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (id) {
            await axios.put(`${API_URL}/${id}`, movie);
        } else {
            await axios.post(API_URL, movie);
        }
        navigate("/movies");
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-500 to-purple-600">
            <form onSubmit={handleSubmit} className="max-w-md w-full p-8 bg-white rounded-lg shadow-lg">
                <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">{id ? "Edit Movie" : "Add Movie"}</h2>
                <input type="text" name="title" value={movie.title} onChange={handleChange} placeholder="Title" required className="w-full p-3 mb-4 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-400" />
                <input type="text" name="genre" value={movie.genre} onChange={handleChange} placeholder="Genre" required className="w-full p-3 mb-4 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-400" />
                <input type="number" name="year" value={movie.year} onChange={handleChange} placeholder="Year" required className="w-full p-3 mb-4 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-400" />
                <input type="text" name="poster" value={movie.poster} onChange={handleChange} placeholder="Poster URL" className="w-full p-3 mb-4 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-400" />
                <input type="text" name="trailer" value={movie.trailer} onChange={handleChange} placeholder="Trailer URL" className="w-full p-3 mb-4 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-400" />
                <input type="text" name="director" value={movie.director} onChange={handleChange} placeholder="Director" className="w-full p-3 mb-4 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-400" />
                <button type="submit" className="w-full bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition duration-200">{id ? "Update" : "Add"} Movie</button>
            </form>
        </div>
    );
};

export default MovieForm;
