import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import '../index.css'; // Importing the main CSS file with Tailwind

const API_URL = "http://localhost:5000/movies";

const MoviesPage = ({ movies, onEdit, onDelete }) => {
    const handleDelete = async (id) => {
        await axios.delete(`${API_URL}/${id}`);
        onDelete(id);
    };

    return (
        <div className="max-w-4xl mx-auto p-4">
            <h2 className="text-2xl font-bold text-center mb-4">Movies List</h2>
            <Link to="/" className="text-blue-500 hover:underline">Add New Movie</Link>
            <ul className="mt-4">
                {movies.map((movie) => (
                    <div key={movie._id}>
                        <li className="mb-4">
                            <p>{JSON.stringify(movie)}</p> {/* Print movies inside UI */}
                        </li>
                
                        <li className="mb-4">
                            <div className="bg-white border border-gray-300 rounded-lg p-4 shadow-md">
                                <h3 className="text-xl font-semibold">{movie.title}</h3>
<img src={movie.poster} alt={`${movie.title} Poster`} className="max-w-xs h-auto rounded" />

                                <p className="text-gray-700">Genre: {movie.genre}</p>
                                <p className="text-gray-700">Year: {movie.year}</p>
                                <a href={movie.trailer} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline">Watch Trailer</a>
                                <div className="mt-2">
                                    <button onClick={() => onEdit(movie)} className="bg-blue-500 text-white px-2 py-1 rounded hover:bg-blue-600">Edit</button>
                                    <button onClick={() => handleDelete(movie._id)} className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600 ml-2">Delete</button>
                                </div>
                            </div>
                        </li>
                    </div>
                ))}
            </ul>
        </div>
    );
};

export default MoviesPage;
