import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import '../index.css'; // Importing the main CSS file with Tailwind

const API_URL = "http://localhost:5000/movies";

const MoviesPage = ({ movies, onEdit, onDelete }) => {
    const handleDelete = async (id) => {
        await axios.delete(`${API_URL}/${id}`);
        onDelete(id);
    };

    return (
        <div className="max-w-4xl  ml-10 p-4">
            <h2 className="text-2xl font-bold text-center mb-4">Movies List</h2>
            <Link to="/" className="text-blue-500 hover:underline">Add New Movie</Link>
            <ul className="mt-4 grid grid-cols-3 gap-10 "> {/* Set grid layout for 5 cards per row */}
                {movies.map((movie) => (
                    <li key={movie._id} className="mb-4">
                        <div className="bg-white border border-gray-300 rounded-lg p-4 shadow-md w-60 h-80 flex flex-col items-center transition-transform transform hover:scale-105"> {/* Set fixed height */}
                            <h3 className="text-xl font-semibold text-center mb-2">{movie.title}</h3>
                            <img src={movie.poster} alt={`${movie.title} Poster`} className="w-32 h-48 object-cover rounded mb-2" /> {/* Adjust image size */}
                            <p className="text-gray-700 text-center">Genre: {movie.genre}</p>
                            <p className="text-gray-700 text-center">Year: {movie.year}</p>
                            <a href={movie.trailer} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline mb-2">Watch Trailer</a>
                            <div className="mt-2 flex justify-between w-full">
                                <button onClick={() => onEdit(movie)} className="bg-blue-500 text-white px-2 py-1 rounded hover:bg-blue-600">Edit</button>
                                <button onClick={() => handleDelete(movie._id)} className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600">Delete</button>
                            </div>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MoviesPage;
