import React from "react";
import { useAuth } from '../context/AuthContext'; // Import the useAuth hook

import { Link } from "react-router-dom";
import axios from "axios";
import '../index.css'; // Importing the main CSS file with Tailwind

const API_URL = "http://localhost:5000/movies";

/**
 * MoviesPage component displays a list of movies and provides options to edit or delete movies for admin users.
 *
 * @param {Object} props - The component props.
 * @param {Array} props.movies - The list of movies to display.
 * @param {Function} props.onEdit - Callback function to handle editing a movie.
 * @param {Function} props.onDelete - Callback function to handle deleting a movie.
 *
 * @returns {JSX.Element} The rendered MoviesPage component.
 */
const MoviesPage = ({ movies, onEdit, onDelete }) => {
    const { user } = useAuth(); // Access user data from context

    const handleDelete = async (id) => {
        await axios.delete(`${API_URL}/${id}`);
        onDelete(id);
    };

    return (
        <div className="max-w-4xl mt-10 mx-auto p-4 bg-gray-100">
            <h2 className="text-3xl font-bold text-center mb-6 text-purple-800">Movies List</h2>
            {user && user.role === 'admin' && (
                <Link to="/add-movie" className="text-blue-600 hover:underline mb-4 block text-center">Add New Movie</Link>
            )}

            <ul className="mt-4 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {movies.map((movie) => (
                    <li key={movie._id} className="mb-4">
                        <div className="bg-blue-500 border border-gray-300 rounded-lg p-4 shadow-lg hover:shadow-xl transition-shadow duration-300 w-full h-85 flex flex-col items-center">
                            <h3 className="text-xl font-semibold text-white text-center mb-2">{movie.title}</h3>
                            <img src={movie.poster} alt={`${movie.title} Poster`} className="w-32 h-48 object-cover rounded mb-2" />
                            <p className="text-white text-center">Genre: {movie.genre}</p>
                            <p className="text-white text-center">Year: {movie.year}</p>
                            <a href={movie.trailer} target="_blank" rel="noopener noreferrer" className="text-yellow-300 hover:underline mb-2">Watch Trailer</a>
                <div className="mt-2 flex justify-between w-full">
                    {user && user.role === 'admin' && (
                        <>
                            <button onClick={() => onEdit(movie)} className="bg-yellow-500 text-white px-3 py-2 rounded hover:bg-yellow-600 transition duration-200">Edit</button>
                            <button onClick={() => handleDelete(movie._id)} className="bg-red-600 text-white px-3 py-2 rounded hover:bg-red-700 transition duration-200">Delete</button>
                        </>
                    )}

                            </div>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MoviesPage;
