import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const API_URL = "http://localhost:5000/movies";

const MovieForm = ({ onSubmit, movies }) => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [movie, setMovie] = useState({ title: "", genre: "", year: "", poster: "", trailer: "", director: "" });

    useEffect(() => {
        if (id) {
            const existingMovie = movies.find((m) => m._id === id);
            if (existingMovie) setMovie(existingMovie);
        }
    }, [id, movies]);

    const handleChange = (e) => {
        setMovie({ ...movie, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (id) {
            await axios.put(`${API_URL}/${id}`, movie);
        } else {
            await axios.post(API_URL, movie);
        }
        navigate("/movies");
    };

    return (
        <form onSubmit={handleSubmit} className="max-w-md mx-auto p-6 bg-white border border-gray-300 rounded-lg shadow-md">
            <h2 className="text-2xl font-bold mb-4 text-center">{id ? "Edit Movie" : "Add Movie"}</h2>
            <input type="text" name="title" value={movie.title} onChange={handleChange} placeholder="Title" required className="w-full p-2 mb-2 border border-gray-300 rounded" />
            <input type="text" name="genre" value={movie.genre} onChange={handleChange} placeholder="Genre" required className="w-full p-2 mb-2 border border-gray-300 rounded" />
            <button type="submit">{id ? "Update" : "Add"} Movie</button>
        </form>
    );
};

export default MovieForm;
