import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import './MoviesPage.css'; // Importing the existing CSS file
import './MoviesPageCard.css'; // Importing the card CSS file

const API_URL = "http://localhost:5000/movies";

const MoviesPage = ({ movies, onEdit, onDelete }) => {
    const handleDelete = async (id) => {
        await axios.delete(`${API_URL}/${id}`);
        onDelete(id);
    };

    return (
        <div>
            <h2>Movies List</h2>
            <Link to="/">Add New Movie</Link>
            <ul>
                {movies.map((movie) => (
                    <li key={movie._id}>
                        <div className="card">
                            <h3>{movie.title}</h3>
                            <p>{movie.genre} ({movie.year})</p>
                            <div>
                                <button onClick={() => onEdit(movie)}>Edit</button>
                                <button onClick={() => handleDelete(movie._id)}>Delete</button>
                            </div>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MoviesPage;
